var preloaderDotsDiv=document.getElementById("preloader-dots"),preloaderDiv=document.getElementById("preloader");function hidePreloader(){preloaderDiv.setAttribute("class","displaynone")}function showPreloader(){preloaderDiv.setAttribute("class","")}function turnOffPreloaderDotsAnimation(){preloaderDotsDiv.setAttribute("class","preloader-dots-static")}function shiftUpPreloader(){turnOffPreloaderDotsAnimation(),$(preloaderDiv).stop().animate({bottom:"100%"},1e3,function(){hidePreloader()})}showPreloader();